{
            // User info
            var userId = User.Identity.GetUserId();
            var schoolId = User.Identity.GetSchoolId();

            // Get the non-apprenticeship activity tracking DTO collection.
            var nonApprenticeshipActivityTrackingDtos =
                _activityTrackingService.GetNonApprenticeshipActivityTrackings(userId, schoolId);

            // Convert the DTO collection into a viewmodel collection.
            var nonApprenticeshipActivityTrackingViewModels = nonApprenticeshipActivityTrackingDtos
                .Select(dto => _mapper.Map<NonApprenticeshipActivityTrackingViewModel>(dto))
                .ToList();

            // Get the apprenticeship activity tracking DTO collection.
            var apprenticeshipActivityTrackingDtos = _activityTrackingService.GetApprenticeshipActivityTrackings(userId, schoolId);

            //// Convert the DTO collection into a viewmodel collection.
            var apprenticeshipActivityTrackingViewModels = apprenticeshipActivityTrackingDtos
                .Select(dto => _mapper.Map<ApprenticeshipActivityTrackingViewModel>(dto))
                .ToList();

            // Build the widget view model.
            var activityTrackingWidgetViewModel = new ActivityTrackingWidgetViewModel()
            {
                NonApprenticeshipActivityTrackings = nonApprenticeshipActivityTrackingViewModels,
                ApprenticeshipActivityTrackings = apprenticeshipActivityTrackingViewModels
            };

            return Ok(activityTrackingWidgetViewModel);
        }

