            var currentUser = User.Identity.GetUser();
            var schoolId = this.GetSchoolId(_db);
            var currentSchool = DataCache.GetSchools(_db).First(e => e.Id == schoolId);

             data.PlanningElements = data.PlanningElements
                .Where(item => item.StartDate != new DateTimeOffset()).ToList();

            var basePath = HttpContext.Current.Server.MapPath("~\\App_Data\\");
            var templatePath = Path.Combine(basePath, "Certifications\\" + currentSchool.Code + "\\planning.docx");
            try
            {
                using (var ms = new MemoryStream())
                {
                    HttpResponseMessage response =  new HttpResponseMessage();
                    var buffer = File.ReadAllBytes(templatePath);
                    ms.Write(buffer, 0, buffer.Length);
                    data.StudentName = $"{currentUser.Nom} {currentUser.Prenom}";
                    var stream = SpecificDocuments.CreatePlanningDocx(ms, data);


                    if (stream != null)
                    {
                        response = new HttpResponseMessage(HttpStatusCode.OK)
                        {
                            Content = new ByteArrayContent(stream.ToArray())
                        };
                        response.Content.Headers.ContentType = new MediaTypeHeaderValue("application/vnd.openxmlformats-officedocument.wordprocessingml.document");
                        response.Content.Headers.ContentDisposition = new ContentDispositionHeaderValue("attachment") { FileName = "planning.docx" };
                    }
                    return response;
                }
            }
            catch (Exception ex)
            {
                Log.Error(ex, "Error trying to print the planning");
                return new HttpResponseMessage(HttpStatusCode.InternalServerError);
            }
        }

