{
            int userId = User.Identity.GetUserId();
            var ecoleId = User.Identity.GetSchoolId();

            List<ConnectionStatsViewModel> stats;
            using (var ts = new TransactionScope(
                 TransactionScopeOption.Required,
                new TransactionOptions
                {
                    IsolationLevel = IsolationLevel.ReadUncommitted,
                    Timeout = TimeSpan.FromSeconds(60)
                }))
            {
                stats = this._db.ReportConnexionHebdoes
                .AsNoTracking()
                .Where(r => r.UserId == userId && r.EcoleId == ecoleId)
                .OrderBy(rch => rch.DateConnexion)
                .Select(rch => new ConnectionStatsViewModel
                {
                    WebInSeconds = rch.DureeWeb,
                    MobileInSeconds = rch.DureeAppli,
                    TotalInSeconds = rch.DureeGlobale,
                    Date = rch.DateConnexion
                })
                .ToList();
            }

            return Ok(stats);
        }

